import Vue from 'vue';
import VueI18n from 'vue-i18n';
import en from './locales/en.json';
import zh from './locales/zh.json';

Vue.use(VueI18n);

const messages = {
    en, // 英文
    zh, // 中文
};

const i18n = new VueI18n({
    locale: 'zh', // 默认语言
    fallbackLocale: 'en', // 回退语言
    messages, // 语言资源
});

export default i18n;
