// src/i18n/index.js
import { createI18n } from 'vue-i18n';
import en from './en';
import zh from './zh';

// 创建 i18n 实例
const i18n = createI18n({
    legacy: false, // 启用 Composition API 模式
    locale: 'en', // 默认语言
    fallbackLocale: 'en', // 回退语言
    messages: {
        en: en,
        zh: zh,
    },
});

export default i18n;
